/**
 * Configures the engine to require/request client authentication.
 * <p/>
 * Created by manishk on 10/2/2015.
 */
export enum ClientAuth {
  NONE,
  REQUEST,
  REQUIRED
}

/**
 * Represents the Cookie SameSite policy to be used. For more info <a href="https://developer.mozilla.org/en-US/docs/Web/HTTP/Cookies#SameSite_cookies">https://developer.mozilla.org/en-US/docs/Web/HTTP/Cookies#SameSite_cookies</a>.
 */
export enum CookieSameSite {
  NONE,
  STRICT,
  LAX
}

/**
 * Represents the possible response codes a server may send after receiving a
 * query. A response code of 0 indicates no error.
 *
 */
export enum DnsResponseCode {
  NOERROR,
  FORMERROR,
  SERVFAIL,
  NXDOMAIN,
  NOTIMPL,
  REFUSED,
  YXDOMAIN,
  YXRRSET,
  NXRRSET,
  NOTAUTH,
  NOTZONE,
  BADVERS,
  BADSIG,
  BADKEY,
  BADTIME
}

/**
 * Represents the version of the HTTP protocol.
 */
export enum HttpVersion {
  HTTP_1_0,
  HTTP_1_1,
  HTTP_2
}

/**
 * The possibles types of {@link JsonEvent} emitted by the {@link JsonParser}.
 */
export enum JsonEventType {
  START_OBJECT,
  END_OBJECT,
  START_ARRAY,
  END_ARRAY,
  VALUE
}

/**
 * The type of a TCP proxy server.
 */
export enum ProxyType {
  HTTP,
  SOCKS4,
  SOCKS5
}

/**
 * Represents the type of reply failure
 */
export enum ReplyFailure {
  TIMEOUT,
  NO_HANDLERS,
  RECIPIENT_FAILURE,
  ERROR
}

/**
 * Policy controlling the behavior across boundaries.
 *
 * This control is applied for clients or servers reporting traces.
 */
export enum TracingPolicy {
  IGNORE,
  PROPAGATE,
  ALWAYS
}

/**
 * Represents the WebSocket version
 */
export enum WebsocketVersion {
  V00,
  V07,
  V08,
  V13
}
